mapper.update:
UPDATE
    mall_commodity_info.t_commodity_attribute
SET

        name = #{param.name}


        is_related = #{param.isRelated}


        is_search = #{param.isSearch}


        is_sale_attribute = #{param.isSaleAttribute}


        is_multiple = #{param.isMultiple}


        is_required = #{param.isRequired}

WHERE
    id = #{param.id}
